const canvasElement=document.getElementById("chartVotingSementara"),liveCountChart=async()=>{let a=(await axios.get("/api/live_count")).data.data,t=[],e=[];a.forEach((a=>{t.push(a.jumlah_suara),e.push(a.nama_kandidat)})),new Chart(canvasElement,{type:"doughnut",data:{labels:e,datasets:[{label:"Hasil Vote",data:t,backgroundColor:["rgb(255, 99, 132)","rgb(54, 162, 235)"],hoverOffset:4}]},options:{responsive:!0,legend:{position:"bottom"},plugins:{datalabels:{formatter:(a,t)=>{let e=0;return t.chart.data.datasets[0].data.map((a=>{e+=parseInt(a)})),(100*a/e).toFixed(2)+"%"},color:"#fff",backgroundColor:"#000"}}}})};liveCountChart();